INSERT INTO `faqs` (`id`, `question`, `answer`, `dd`) VALUES
(1, '8 жилийн өмнө төгсөөд ажиллаж эхэлсэн би одоо шууд сургуульд орохоор бүртгүүлж болох уу?', 'Та ЭЕШ-н шалгалтыг заавал өгсөн байх шаардлагатай бөгөөд авсан оноогоор тухайн сургуулийн босго оноонд хүрсэнээр хүссэн сургуульдаа бүртгүүлж болно.', 0),
(3, 'Аль сургууль сонгох вэ?', 'Та сургуулийг нэр хүндээс гадна төгсөгдчийн ажил эрхлэлт, багш нарын чадавхи, сурах орчиныг сайн судлах нь хэрэгтэй. Сонголтоо хийхээс өмнө та ямар мэргэжилтэй болохоо сонгосоноор та аль сургуулийг тухайн мэргэжилийг сайн олгож байна гэдгээр нь сонгоход хялбар.', 0),
(4, 'Сонгууль хэзээ болох вэ?', '2020-10-15 нд болно', 1),
(5, 'Монгол үхрийн мах хэдтэй байна вэ?', '8000 төг', 2),
(6, 'Би хэн бэ?', 'Тамир', 100);

