INSERT INTO `news` (`id`, `title`, `category`, `brief`, `content`, `image`, `thumb`, `timestamp`, `date`, `visited`) VALUES
(1, 'Хан-Уул дүүргийн удирдлагууд Монгол улсын Шинэ үндсэн хууль батлалцсан дeпутатуудаа хүлээн авч хүндэтгэл үзүүллээ.', 6, 'Уг ойн арга хэмжээний хүрээнд дүүргийн ИТХ-ын дарга Б.Цэрэн. Засаг даргын нэгдүгээр орлогч Г.Батсайхан. Тэргүүлэгчдийн Ажлын албаны хэлтсийн дарга нар тус дүүрэгт оршин суудаг дeпутатуудыг хүлээн авч хүндэтгэл үзүүллээ.', '<p>2020 оны 01 дүгээр сарын 13-ны өдөр Шинэ үндсэн хууль батлагдсаны 28 жилийн ой тохиож байна. Уг ойн арга хэмжээний хүрээнд дүүргийн ИТХ-ын дарга Б.Цэрэн. Засаг даргын нэгдүгээр орлогч Г.Батсайхан. Тэргүүлэгчдийн Ажлын албаны хэлтсийн дарга нар тус дүүрэгт оршин суудаг дeпутатуудыг хүлээн авч хүндэтгэл үзүүллээ.</p><p>Уг арга хэмжээг нээж дүүргийн ИТХ-ын дарга Б.Цэрэн үг хэлж, Засаг даргын нэгдүгээр орлогч Г.Батсайхан,&nbsp; Монгол улсын Eрөнхий сайд асан Ш.Гунгаадорж нар сэтгэгдлээ хуваалцлаа.</p><p>Шинэ Үндсэн хуулийг батлалцсан эрхэм&nbsp;дeпутат&nbsp;Ш.Гунгаадорж, З.Алтай, Р.Хатанбаатар, Ц.Адъяасүрэн, Т.Гантөмөр, Б.Буяндэлгэр, Н.Төмөртогоо, Д.Тогооч, Л.Цог, П.Энхсайхан, З.Адьяа, Р.Батсүх, Т.Төмөрбаатар, Ж.Адъяа нар хүндэтгэл үзүүлэх арга хэмжээнд оролцов.</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1992 онд батлагдсан Шинэ үндсэн хууль Монгол улсын төр,нийгэм эдийн засаг,улс төрийн бүхий л салбарт үүргээ гүйцэтгэж, үнэ цэнэтэй хэвээр байгааг дeпутатууд онцлон дурдаж байлаа.</p>', 0x75706c6f6164732f3230323130372f30373133343634366e6577732e6a7067, '', '2020-01-13 16:00:00', NULL, 0),
(2, 'ХАН-УУЛ ДҮҮРГИЙН ИТХ-ЫН ТЭРГҮҮЛЭГЧДИЙН ЭЭЛЖИТ ХУРАЛДААН БОЛЛОО', 6, '1. Тодорхойлолтыг дэмжих тухай\r\n2. Хан-Уул дүүргийн Иргэдийн Төлөөлөгчдийн хурлын Тэргүүлэгчдийн Ажлын албаны 2019 онд хийж гүйцэтгэгдсэн ажлын тайлан\r\n3. Хан-Уул дүүргийн иргэдийн Төлөөлөгчдийн хурлын Тэргүүлэгчдийн Ажлын албаны 2020 оны Гүйцэтгэлийн төлөвлөгөөний тухай', '<figure class=\"image\"><img src=\"http://103.17.109.33/medias/940c6817-4abd-4fbd-8ab2-1e8f2d22f54a.jpg?w=850\"></figure><p>Хан-Уул дүүргийн ИТХ-ын Тэргүүлэгчид 2020 оны 1 дүгээр сарын 16-ны өдөр ээлжит хуралдаанаа хийж дараах асуудлыг хэлэлцлээ.</p><p>1. Тодорхойлолтыг дэмжих тухай<br>2. Хан-Уул дүүргийн Иргэдийн Төлөөлөгчдийн хурлын Тэргүүлэгчдийн Ажлын албаны 2019 онд хийж гүйцэтгэгдсэн ажлын тайлан<br>3. Хан-Уул дүүргийн иргэдийн Төлөөлөгчдийн хурлын Тэргүүлэгчдийн Ажлын албаны 2020 оны Гүйцэтгэлийн төлөвлөгөөний тухай</p>', 0x75706c6f6164732f3230323130372f3037313634303433336e657773322e6a7067, '', '2020-01-16 22:16:40', NULL, 0),
(3, 'ХАН-УУЛ ДҮҮРГИЙН ИТХ-ЫН ТЭРГҮҮЛЭГЧИД ХУРАЛДЛАА', 6, 'Монгол улсын “Гавъяат малын эмч”, “Гавъяат агрономич”, “Гавъяат дасгалжуулагч”, “Аж үйлдвэрийн гавъяат ажилтан”, “Гавъяат багш”, “Соёлын гавъяат зүтгэлтэн” “Гавъяат эдийн засагч”, “Гавъяат жүжигчин” цолоор, “Хөдөлмөрийн гавъяаны улаан тугийн одон”, “Алтан гадас одон”, “Цэргийн гавъяаны одон”, “Хөдөлмөрийн хүндэт медаль”-иар тус тус шагнуулахаар ', '<p>Хан-Уул дүүргийн ИТХ-ын Тэргүүлэгчид ээлжит хуралдаанаа хийлээ.<br>Дүүргийн Засаг даргын өргөн мэдүүлсэн саналын дагуу төрийн болон төрийн бус байгууллага, боловсрол, эрүүл мэнд, эрчим хүч, хөнгөн үйлдвэрийн салбарт олон жил үр бүтээлтэй ажиллаж тодорхой амжилт гаргасан иргэдийг Монгол улсын “Гавъяат малын эмч”, “Гавъяат агрономич”, “Гавъяат дасгалжуулагч”, “Аж үйлдвэрийн гавъяат ажилтан”, “Гавъяат багш”, “Соёлын гавъяат зүтгэлтэн” “Гавъяат эдийн засагч”, “Гавъяат жүжигчин” цолоор, “Хөдөлмөрийн гавъяаны улаан тугийн одон”, “Алтан гадас одон”, “Цэргийн гавъяаны одон”, “Хөдөлмөрийн хүндэт медаль”-иар тус тус шагнуулахаар тодорхойлолтыг дэмжиж холбогдох газар уламжлахаар шийдвэрлэлээ.<br>Мөн Монгол Улсын Их Хурлын сонгуулийн тухай хуулийн 13 дугаар зүйлийн 13.1 дэх хэсгийг үндэслэж УИХ-ын 2020 оны ээлжит сонгуулийн 29 дүгээр тойргийн сонгуулийн хэсэг, нутаг дэвсгэр, төвийг 60 хэсэгтэй байхаар тогтоож хуулийн хэрэгжилтийг ханган ажиллалаа.</p>', 0x75706c6f6164732f3230323130372f30373137343237346e657773332e6a7067, '', '2020-02-19 22:17:42', NULL, 4),
(4, 'НИЙТИЙН СОНСГОЛ ЗОХИОН БАЙГУУЛЛАА', 6, '2020 оны 07 дугаар сарын 22-ны өдөр дүүргийн иргэдийн Төлөөлөгчдийн Хурлын Тэргүүлэгчдийн хуралдаанаар хэлэлцэн Нийтийн сонсгол зохион байгуулах тухай 30 дугаарт тогтоолыг гаргасан.', '<p>&ldquo;ПИ АЙ ЭМ ЭМ&rdquo; ХХК нь ногоон байгууламж төлөвлөгдсөн газар орон сууц барихаар барилгын ажил эхлүүлсэн нь Ханбогд ресидэнс хорооллын оршин суугчдын эрх ашгийг хөндөж байгаа тухай тус хорооллын иргэдийн гаргасан өргөдлийн дагуу 2020 оны 07 дугаар сарын 22-ны өдөр дүүргийн иргэдийн Төлөөлөгчдийн Хурлын Тэргүүлэгчдийн хуралдаанаар хэлэлцэн Нийтийн сонсгол зохион байгуулах тухай 30 дугаарт тогтоолыг гаргасан.<br>Тогтоолын дагуу 2020 оны 07 дугаар сарын 26-ны өдөр Хан-Уул дүүргийн 4 дүгээр хорооны 34 дүгээр сургуулийн зааланд хяналтын сонсголыг зохион байгууллаа.<br>Сонсголыг Хан-Уул дүүргийн ИТХ-ын дарга Б.Цэрэн даргалж Хан-Уул дүүргийн Засаг даргын 1-р орлогч Г.Батсайхан, 4-р хорооны засаг дарга О.Ганхуяг, дүүргийн ИТХ-ын Төлөөлөгч О.Баярсайхан нар болон &quot;Пи Ай Эм Эм&quot; ХХК-ийн захирал Н.Мэндсайхан, барилгын инженер Н.Сандагдорж, Ханбогд хорооллын оршин суугчдын төлөөлөл 393 иргэд оролцсон.<br>ИТХ-ын дарга Б.Цэрэн Нийтийн сонсголын тухай хууль, Нийтийн сонсгол зохион байгуулах дэгийг танилцуулснаар үйл ажиллагаа эхэллээ. Ханбогд ресиденс хорооллын оршин суугчид ногоон байгууламж төлөвлөсөн газар хууль зөрчиж орон сууцны барилгын ажил эхлүүлсэн нь иргэдийн эрүүл аюулгүй амьдрах эрх зөрчигдсөн, машины зогсоол байхгүй, оршин суугчид нэмэгдсэнээр цэцэрлэг сургуулийн хүртээмж улам хүрэлцэхгүй болох зэргээр уг барилгын ажлыг эсэргүүцэж байна. Тус компани анхны хорооллыг барихад цэцэрлэг барихаар төлөвлөсөн байсан ч өндөр төлбөртэй хувийн цэцэрлэг барьсан тул оршин суугчдын шаардлагыг хангахгүй байгаа юм. Мөн энэ газар улсын цэцэрлэг бариулах хүсэлтэй байгаа тухай өөрсдийн байр сууриа илэрхийлж байсан юм.<br>Оролцогчид саналаа сонсголын явцад амаар болон санал асуулгын хуудас, онлайн хуудсуудад коммэнт хэлбэрээр өгсөн.<br>Нийт санал өгсөн иргэдийн 78.5 хувь нь барилга бариулахыг дэмжихгүй, 15.2 хувь нь дэмжиж, 6.3 хувь нь санал тэмдэглээгүй хуудас өгсөн байна.<br>Иргэд 203 болон 302 дугаар байрны гадна талбайд орон сууц барих асуудлыг дэмжихгүй газрын зөвшөөрлийг цуцлуулах хүсэлт гаргасан байна.<br>Дүүргийн Засаг даргын 1-р орлогч Г.Батсайхан ногоон байгууламж сүйтгэж, иргэдийн эрх, ашиг хөндөгдсөн хэд хэдэн газрын зөвшөөрлийг хүчингүй болгуулсан. Бид иргэдийнхээ ая тухтай амьдрах орчин нөхцлийг бүрдүүлэхийн төлөө ажилласаар байгаа. Тиймээс Хан-Уул дүүргийн Засаг даргын &ldquo;Пи Ай Эм Эм&rdquo; ХХК-ын орон сууц барих зөвшөөрлийг цуцлах тухай албан бичгийг Нийслэлийн Засаг дарга бөгөөд Улаанбаатар хотын захирагчийн үүрэг гүйцэтгэгч Ж.Батбаясгаланд хүргүүлсэн гэв.<br>ИТХ-ын дарга Б.Цэрэн нийтийн сонсгол нь үйл ажиллагааны талаар байр сууриа илэрхийлэх боломжийг олгохын зэрэгцээ, шийдвэр гаргагчдад өөрсдийн дуу хоолойгоо хүргэх томоохон шийдэл юм. Иймээс энэхүү сонсголын үр дүнд иргэд олон нийтийн хүсэлтийг харгалзан цэцэрлэг бариулах тал дээр анхаарах болно гэдгээ илэрхийллээ.</p><figure class=\"image\"><img src=\"http://103.17.109.33/medias/44cea9df-5638-49fa-8cbf-3ac92a5abda8.JPG?w=850\" alt=\"\" class=\"fr-fic fr-dii\"></figure><figure class=\"image\"><img src=\"http://103.17.109.33/medias/3b84c6e0-0def-422f-8f85-47371df54020.JPG?w=850\" alt=\"\" class=\"fr-fic fr-dii\"></figure><figure class=\"image\"><img src=\"http://103.17.109.33/medias/522230e2-364b-4319-b636-30283b1c89c6.JPG?w=850\" alt=\"\" class=\"fr-fic fr-dii\"></figure><figure class=\"image\"><img src=\"http://103.17.109.33/medias/a621d49a-4ecb-42fe-a15b-b49f8db6523d.JPG?w=850\" alt=\"\" class=\"fr-fic fr-dii\"></figure><figure class=\"image\"><img src=\"http://103.17.109.33/medias/b42aa2c0-7484-4c75-b6e2-f8e23bdc9a69.JPG?w=850\" alt=\"\" class=\"fr-fic fr-dii\"></figure><figure class=\"image\"><img src=\"http://103.17.109.33/medias/37e7d590-435f-43ec-88f9-d5f194251ae1.JPG?w=850\" alt=\"\" class=\"fr-fic fr-dii\"></figure><figure class=\"image\"><img src=\"http://103.17.109.33/medias/f814b9c7-a636-4ce3-8dea-a71bae8e3e65.JPG?w=850\" alt=\"\" class=\"fr-fic fr-dii\"></figure><p>&nbsp;</p>', 0x75706c6f6164732f3230323131302f3037313730383739336f72672e4a5047, '', '2020-07-25 22:19:37', '2021-10-04', 4);

